    cmdName := "echo hello && echo 'command inject'"
    //未判断外部输入是否存在命令注入字符，结合sh可造成命令注入
    cmd := exec.Command("sh", "-c", cmdName)
    output, _ := cmd.CombinedOutput()
    fmt.Println(string(output))

    cmdName := "ls"
    //未判断外部输入是否是预期命令
    cmd := exec.Command(cmdName)
    output, _ := cmd.CombinedOutput()
    fmt.Println(string(output))
}

// good
func foo() {
    cmdName := "echo hello && echo 'command inject'"
    // 限制命令字符内容
    matched, _ := regexp.Match(`^[a-zA-Z0-9 ]+$`, []byte(cmdName))
    if !matched {
        fmt.Println("command illegal")
        return
    }
    cmd := exec.Command("sh", "-c", cmdName)
    output, _ := cmd.CombinedOutput()
    fmt.Println(string(output))
}
