func handler(w http.ResponseWriter, r *http.Request) {
  path := r.URL.Query()["path"][0]

  // 未过滤文件路径，可能导致任意文件读取
  data, _ := ioutil.ReadFile(path)
  w.Write(data)

  // 对外部传入的文件名变量，还需要验证是否存在../等路径穿越的文件名
